import React , {useState} from 'react'; 
import ExpenseForm from './ExpenseForm'; 
import './NewExpense.css';

const NewExpense = (props)=>{   
    const [hideExpense,hideExpenseHandler] = useState('True'); 

    const getExpense = (enteredExpense)=>{ 
        const curData = {
            ...enteredExpense , 
            id : Math.random().toString()
        };  
        props.onSaveExpenseData(curData);
    }  

    const showExpense = () =>{
        if(hideExpense === 'False'){
            hideExpenseHandler('True');
        }else{
            hideExpenseHandler('False');
        }  
        console.log(hideExpense);
    }  
    let a;
    if(hideExpense === 'True'){
         a = <button onClick={showExpense}>Add new Expense</button>
    }else{
         a = <ExpenseForm onSaveExpenseData={getExpense}></ExpenseForm>
    }
    return ( 
        <div className="new-expense"> 
            {a}
            
        </div>
        
    );
} 

export default NewExpense;
