import React , {useState} from 'react';
import ExpenseItem from './ExpenseItem';  
import Card from '../UI/Card'; 
import ExpensesFilter from './ExpensesFilter';
import './ExpenseBlock.css';

function ExpenseBlock(props){ 
    const [newYear , newYearHandler] = useState('2020');
    const onYearChange = (selectedYear)=>{ 
        newYearHandler(selectedYear);
    }

    return(  
        <div> 
            <Card className="expenses"> 
            <ExpensesFilter defaultYear ={newYear} onYearChanging={onYearChange}></ExpensesFilter> 
            {props.item.map( (exp) => <ExpenseItem 
                    title = {exp.title} 
                    date = {exp.date} 
                    amount= {exp.amount}>
            </ExpenseItem>)}
            {/* <ExpenseItem
                title = {props.item[0].title} 
                date = {props.item[0].date} 
                amount = {props.item[0].amount}
            ></ExpenseItem>
            <ExpenseItem
                title = {props.item[1].title} 
                date = {props.item[1].date} 
                amount = {props.item[1].amount}
            ></ExpenseItem>
            <ExpenseItem
                title = {props.item[2].title} 
                date = {props.item[2].date} 
                amount = {props.item[2].amount}
            ></ExpenseItem>
            <ExpenseItem
                title = {props.item[3].title} 
                date = {props.item[3].date} 
                amount = {props.item[3].amount}
            ></ExpenseItem>  */}
        </Card> 
      </div>
    );
} 
export default ExpenseBlock;
